import React from 'react'
import 'react-daterange-picker/dist/css/react-calendar.css'
import {getTripsApi} from './api'
import Container from '@material-ui/core/Container'
import Paper from '@material-ui/core/Paper'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'


class App extends React.Component {
	state = {
		tags: [],
		isSearchClicked: false,
		trips: []
	}


	onAddTag = tag => {
		const newTags = this.state.tags.concat(tag)
		this.setState({tags: newTags})
	}

	onDeleteTag = tag => {
		const newTags = this.state.tags.filter(elem => elem !== tag)
		this.setState({tags: newTags})
	}

	handleOpenResult = () => {
		this.setState({isSearchClicked: true})
	}

	getTrips = async (by = 'PRICE', order = 'ASCENDING') => {
		try {
			const trips = await getTripsApi(by, order)
			if (Array.isArray(trips)) {
				this.setState({trips})
			}
		} catch (error) {
			console.log({error})
		}
	}

	render() {
		return (
			<div id="App">
				<Container className={'root'}>
					<Paper elevation={3} variant={'outlined'} className={'main'}>
						<Typography component={'h3'}>Find your best trip ever</Typography>
					</Paper>
				</Container>
			</div>
		)
	}
}

export default App
