import React from 'react'
import API from './api'
import 'react-date-range/dist/styles.css' // main style file
import 'react-date-range/dist/theme/default.css' // theme css file
import {DateRangePicker} from 'react-date-range'
import Icon from '@material-ui/core/Icon';

import {
	AppBar,
	Paper,
	Typography,
	Button,
	Toolbar,
	Card,
	CardActions,
	CardMedia,
	CardContent,
	Slider,
	TextField,
	IconButton
} from '@material-ui/core'
import Autocomplete from '@material-ui/lab/Autocomplete'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore'
import Collapse from '@material-ui/core/Collapse'
import List from '@material-ui/core/List'
import ListItem from '@material-ui/core/ListItem'
import ListItemText from '@material-ui/core/ListItemText'
import ListItemIcon from '@material-ui/core/ListItemIcon'

class App extends React.Component {
	state = {
		tags: [],
		isSearchClicked: false,
		trips: []
	}

	getTrips = async (by = 'PRICE', order = 'ASCENDING') => {
		try {
			const trips = await API.getTripsApi(by, order)
			if (Array.isArray(trips)) {
				this.setState({trips})
			}
		} catch (error) {
			console.log({error})
		}
	}
	allTags = []

	async componentDidMount() {
		let tag = await API.getAllTags()
		let cnt = await API.getAllCountries()
		this.setState({...this.state, tags: tag, countries: cnt})
	}

	constructor() {
		super()
		this.state = {
			tags: [],
			countries: [],
			value: [1000, 2000],
			cards: [],
			expanded: false,
			selectionRange: {
				startDate: new Date(),
				endDate: new Date(),
				key: 'selection'
			}
		}
	}

	handleChange = (event, newValue) => {
		this.setState({...this.state, value: newValue})
	}
	handleSelect = (ranges) => {
		console.log(ranges)
		this.setState({...this.state, selectionRange: {...(ranges.selection), key: this.state.selectionRange.key}})
	}

	handleExpandClick = () => {
		this.setState({...this.state, expanded: !this.state.expanded})

	}

	search = () => {
		console.log(1)
	}

	render() {

		function valuetext(value) {
			return `${value}$`
		}

		return (
			<div id="App">
				<AppBar className={'root'}>
					<Toolbar>
						<Typography variant="h6" className={'title'}>
							Travel Agency
						</Typography>
						<Button color="inherit" variant={'outlined'}>Admin</Button>
					</Toolbar>
				</AppBar>
				<Paper elevation={3} variant={'outlined'} className={'main'}>
					<div className="filter">
						<span id={'filter'}>Filter</span>
						<span className={'select-data'}>Select data range:</span>
						<div className={'date'}>
							<DateRangePicker
								editableDateInputs={true}
								ranges={[this.state.selectionRange]}
								onChange={this.handleSelect}
								moveRangeOnFirstSelection={false}
							/>
							<div className="params">
								<Autocomplete
									multiple
									id="tags-standard"
									options={this.state.tags}
									getOptionLabel={(option) => option.title}
									renderInput={(params) => (
										<TextField
											{...params}
											variant="standard"
											label="Select tags"
											placeholder="Tags"
										/>
									)}
								/>
								<span className={'spacer'}/>
								<Autocomplete
									multiple
									id="cntrs-standard"
									options={this.state.countries}
									getOptionLabel={(option) => option.title}
									renderInput={(params) => (
										<TextField
											{...params}
											variant="standard"
											label="Select countries"
											placeholder="Countries"
										/>
									)}
								/>
								<span className="spacer"/>
								<div>
									<Typography id="range-slider" gutterBottom>
										Price range
									</Typography>
									<Slider
										className={'slider'}
										// marks={true}
										value={this.state.value}
										min={0}
										max={5000}
										onChange={this.handleChange}
										aria-labelledby="range-slider"
										getAriaValueText={valuetext}
										valueLabelDisplay="on"
									/>
									<Typography>
										Price from {this.state.value[0]}$ to {this.state.value[1]}$
									</Typography>
								</div>


								{/*<ChipInput*/}
								{/*	value={tags}*/}
								{/*	onAdd={(chip) => handleAddChip(chip)}*/}
								{/*	onDelete={(chip, index) => handleDeleteChip(chip, index)}*/}
								{/*	dataSource={allTags}*/}
								{/*	helperText={'Select Tags'}*/}

								{/*/>*/}
							</div>

						</div>
						<Button
							className={'button-search'}
							variant={'outlined'}
							color={'primary'}
							onClick={this.search}
						>Search</Button>

					</div>
				</Paper>
				<Paper className={'result'} variant={'outlined'} elevation={2}>
					{this.state.cards}
					<Card className={'card'} variant={'outlined'}>
						<CardMedia
							className={'image'}
							image={'https://picsum.photos/200/300'}
							title={'name'}
						/>
						<CardContent>
							<Typography variant={'h4'}>Title</Typography>
							<Typography variant={'body2'}>text</Typography>
							<List component="nav" aria-label="contacts">
								<ListItem button>
									<ListItemIcon>
										<Icon>attach_money</Icon>
									</ListItemIcon>
									<ListItemText primary="100" />
								</ListItem>
								<ListItem button>
									<ListItemIcon>
										<Icon>attach_money</Icon>
									</ListItemIcon>
									<ListItemText primary="Eric Hoffman" />
								</ListItem>
							</List>
							<Typography variant={'h4'}>Price</Typography>
							<span>100$</span>
							<span className="spacer" />
							<Typography variant={'h4'}>Date</Typography>
							<span>10.05.2020 - 10.06.2020</span>
							<Typography variant={'h4'}>Countries</Typography>
							<List component="nav" aria-label="contacts">
								<ListItem button>
									<ListItemText primary="Chelsea Otakan" />
								</ListItem>
								<ListItem button>
									<ListItemText primary="Eric Hoffman" />
								</ListItem>
							</List>
						</CardContent>
						<CardActions disableSpacing className={'actions'}>
							<Button variant={'outlined'} className={'more-button'} href={'https://google.com'}>More</Button>
						</CardActions>
					</Card>
				</Paper>
			</div>
		)
	}
}

export default App
