          <div className="tagContainer">
            {this.props.tags.map(elem => (
              <div className="tagWrapper">
                <div className="tag">{elem}</div>
                <div
                  className="removeTagCross"
                  onClick={() => {
                    this.props.removeTag(elem);
                  }}
