import React from 'react'
import API from './api'
import 'react-date-range/dist/styles.css' // main style file
import 'react-date-range/dist/theme/default.css' // theme css file
import ChipInput from 'material-ui-chip-input'

import {DateRangePicker} from 'react-date-range'
import {AppBar, Paper, Typography, Button, Toolbar} from '@material-ui/core'

class App extends React.Component {
	state = {
		tags: [],
		isSearchClicked: false,
		trips: []
	}

	onAddTag = tag => {
		const newTags = this.state.tags.concat(tag)
		this.setState({tags: newTags})
	}

	onDeleteTag = tag => {
		const newTags = this.state.tags.filter(elem => elem !== tag)
		this.setState({tags: newTags})
	}

	handleOpenResult = () => {
		this.setState({isSearchClicked: true})
	}

	getTrips = async (by = 'PRICE', order = 'ASCENDING') => {
		try {
			const trips = await API.getTripsApi(by, order)
			if (Array.isArray(trips)) {
				this.setState({trips})
			}
		} catch (error) {
			console.log({error})
		}
	}

	render() {
		const selectionRange = {
			startDate: new Date(),
			endDate: new Date(),
			key: 'selection'
		}
		let tags = []
		const allTags = ['winter', 'summer']

		function handleAddChip(chip) {
			tags.push(chip)
		}

		function handleDeleteChip(chip, index) {
			console.log(chip, index)
			tags[index] = undefined
			let new_tags = []
			for (let i = 0; i < tags.length; i++) {
				if (tags[i] === undefined) new_tags.push(tags[i])
			}
			tags = new_tags
		}

		function handleSelect(ranges) {
			console.log(ranges)
			selectionRange.endDate = ranges.selection.endDate
			selectionRange.startDate = ranges.selection.startDate
			// {
			//   selection: {
			//     startDate: [native Date Object],
			//     endDate: [native Date Object],
			//   }
			// }
		}

		return (
			<div id="App">
				<AppBar className={'root'}>
					<Toolbar>
						<Typography variant="h6" className={'title'}>
							Travel Agency
						</Typography>
						<Button color="inherit" variant={'outlined'}>Admin</Button>
					</Toolbar>
				</AppBar>
				<Paper elevation={3} variant={'outlined'} className={'main'}>
					<div className="filter">
						<span id={'filter'}>Filter</span>
						<span className={'select-data'}>Select data range:</span>
						<div className={'date'}>
							<DateRangePicker
								editableDateInputs={true}
								ranges={[selectionRange]}
								onChange={handleSelect}
								moveRangeOnFirstSelection={false}
							/>
							<div className="params">
								<ChipInput
									value={tags}
									onAdd={(chip) => handleAddChip(chip)}
									onDelete={(chip, index) => handleDeleteChip(chip, index)}
									dataSource={allTags}
									helperText={'Select Tags'}

								/>
							</div>
						</div>
					</div>
				</Paper>
				<Paper className={'result'} variant={'outlined'} elevation={2}>

				</Paper>
			</div>
		)
	}
}

export default App
